var getFrequencies = require('getFrequencies');
var flattenArray = require('flattenArray');

var getMostActiveContributors = function(array){
	
	// Create an array from the 'edits' objects in the data
	var padEdits = [];
	array.forEach( function (pad){
		padEdits.push(flattenArray(pad.padEdits));
	});

	// return the top 10 frequencies
	return getFrequencies(padEdits).slice(0, 9);
}

module.exports = getMostActiveContributors;