// getPadEditHistory
//
// Returns a list of authors who have edited a pad.
// An author appears in the list each time they make an edit.

var async = require('async');
var getPadTitle = require('getPadTitle');
var getPadEdits = require('getPadEdits');

var getPadData = function(padId, callback){

	async.parallel({
	    padId: function(callback){

	        callback(null, padId);

	    },
	    padTitle: function(callback){

	    	getPadTitle(padId, function(err, padTitle) {
	        	callback(null, padTitle);
	    	});
	    },
	    padEdits: function(callback){

	    	getPadEdits(padId, function(err, padEdits) {
	        	callback(null, padEdits);
	    	});
	    }
	},
	function(err, pad) {
	    callback(null, pad);
	});




}

module.exports = getPadData