// getPadEditHistory
//
// Returns a list of authors who have edited a pad.
// An author appears in the list each time they make an edit.

var login = require('login');

var getPadEdits = function(padId, callback){
	// Get the revisions for that pad
	login.hackpad.revisions(padId, function(err, resp) {

		if(err){ console.log('Error!'); return; }

		var padEdits = [];

		// Get a list of the edits made by authors
		resp.map( function(item) {
			padEdits.push(item.authors);
		})

		callback(err, padEdits);

	});
}

module.exports = getPadEdits