var login = require('login');

var getPadTitle = function(padId, callback){

	// Get the pad contents
	login.hackpad.get('/pad/'+padId+'/content/latest.txt', function(err, resp){

		if(err){ console.log('Error!'); return; }

		// The first line of the content is the title
		var title = resp.split('\n')[0];
		callback(null, title);

	});
}

module.exports = getPadTitle;